const o=document.getElementById("theme-toggle"),d=document.documentElement;o.addEventListener("click",()=>{const e=d.classList.toggle("dark");localStorage.setItem("theme",e?"dark":"light")});const n=document.getElementById("mobile-menu-button"),t=document.getElementById("mobile-menu");n.addEventListener("click",e=>{e.stopPropagation(),t.classList.toggle("hidden")});document.addEventListener("click",e=>{!t.contains(e.target)&&!n.contains(e.target)&&t.classList.add("hidden")});t.querySelectorAll("a").forEach(e=>{e.addEventListener("click",()=>{t.classList.add("hidden")})});if(typeof localStorage<"u"){const e=localStorage.getItem("theme")||(window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light");document.documentElement.classList.toggle("dark",e==="dark")}
